// Passing buy() will force the immediate execution of the function
// Passing buy is a pointer that will ONLY get executed when the button is pressed
// 
// onPressed: buy
//
//  void buy() {
//    print('Pressed');
//  }
//
// On the other hand, be careful with fat arrow/anonymous functions, they expect a function and NOT the pointer
//  onSubmitted: (_) => _submitData(),
// Note that (_) the underscore is a way to say that we don't care about the argument

TextButton(
  style: ButtonStyle(
    foregroundColor: MaterialStateProperty.all<Color>(Colors.blue),
    overlayColor: MaterialStateProperty.resolveWith<Color>(
      (Set<MaterialState> states) {
        if (states.contains(MaterialState.hovered))
          return Colors.blue.withOpacity(0.04);
        if (states.contains(MaterialState.focused) ||
            states.contains(MaterialState.pressed))
          return Colors.blue.withOpacity(0.12);
        return null; // Defer to the widget's default.
      },
    ),
  ),
  onPressed: () { },
  child: Text('TextButton')
)

TextButton(
  style: ButtonStyle(
    overlayColor: MaterialStateProperty.resolveWith<Color>(
      (Set<MaterialState> states) {
        if (states.contains(MaterialState.focused))
          return Colors.red;
        return null; // Defer to the widget's default.
      }
    ),
  ),
  onPressed: () { },
  child: Text('TextButton'),
)

