void _startAddNewTransaction(BuildContext context) {
   showModalBottomSheet(
       context: context,
       builder: (_) {
         return GestureDetector(
           onTap: () {},
           child: NewTransaction(newTransactionHandler: _addNewTransaction),
           behavior: HitTestBehavior.opaque,
         );
       });
 }

