double resizeHeight(BuildContext context, AppBar appBar, double ratio) {
  return MediaQuery.of(context).size.height * ratio -
      // Subtract appBar
      appBar.preferredSize.height -
      // Subtract status bar 
      MediaQuery.of(context).padding.top;
}

