// import 'dart:io';

// Platform.isAndroid
// Platform.isIOS

// Widget tree: everything gets recreated

// Element tree: never get recreated. It keeps a reference of the widgets and updates 
//               its reference to point to the widget that has been rebuilt
//               then, it signals to the render tree to only 
//               rerenders the delta.

// Render tree: responsible for painting the widgets on the screen

// 1) setState
// 2) MediaQuery (doesn't care which function is called)
// 3) Theme update

// Stateless Widgets
// Constructor function -> build()

// Statefule Widgets
// Constructor function -> initState() -> build() -> setState() -> didUpdateWidget() -> build() -> dispose() (in case you conditionally changed the widget(s))

// Inactive -> no user input is received
// Paused -> running in the background
// Resumed 
// Suspended -> about to be terminated

@override
void initState() {
  WidgetsBinding.instance!.addObserver(this);
  super.initState();
}

@override
void dispose() {
  WidgetsBinding.instance!.removeObserver(this);
  super.dispose();
}

