// Description: A Flutter geolocation plugin which provides easy access to platform specific location services
// URL:https://pub.dev/packages/geolocator
// Install: flutter pub add geolocator
// App: clima (src/mobile/flutter/learning/udemy/complete-flutter-app-development-bootcamp-with-dart/section-13/clima)

// Example
// Add this to a services
import 'package:geolocator/geolocator.dart';

class Location {
  double latitude = 0.0;
  double longitude = 0.0;

  Future<void> getCurrentLocation() async {
    try {
      Position position = await Geolocator.getCurrentPosition(
          desiredAccuracy: LocationAccuracy.low);
      latitude = position.latitude;
      longitude = position.longitude;
    } catch (e) {
      print(e);
    }
  }
}

