// App: src/mobile/flutter/learning/udemy/flutter-dart-complete-guide/flutter_shop
// With rollback!
  Future<void> toggleFavoriteStatus() async {
    final oldStatus = isFavorite;
    isFavorite = !isFavorite;
    notifyListeners();

    final updateUrl =
        'https://flutter-shop-aziz-default-rtdb.firebaseio.com/products/$id.json';
    try {
      final response = await http.patch(Uri.parse(updateUrl),
          body: json.encode({
            'isFavorite': isFavorite,
          }));
      if (response.statusCode >= 400) {
        _setFatValue(oldStatus);
      }
    } catch (e) {
      _setFatValue(oldStatus);

      print(e);
    }
  }

  void _setFatValue(bool newValue) {
    isFavorite = newValue;
    notifyListeners();
  }

