import 'dart:math';

import 'package:animation_grider/src/widgets/cat.dart';
import 'package:flutter/material.dart';

class Home extends StatefulWidget {
  @override
  State<Home> createState() => _HomeState();
}

class _HomeState extends State<Home> with TickerProviderStateMixin {
  late Animation<double> _catAnimation;
  late AnimationController _catController;
  late Animation<double> _boxAnimation;
  late AnimationController _boxController;

  @override
  void initState() {
    super.initState();
    _boxController = AnimationController(
      duration: const Duration(
        milliseconds: 300,
      ),
      vsync: this,
    );

    // Defines the range of values for our animation to play beTween
    _boxAnimation = Tween(begin: pi * 0.60, end: pi * 0.65,)
        .animate(
      CurvedAnimation(
        parent: _boxController,
        curve: Curves.easeInOut,
      ),
    );
    _boxAnimation.addStatusListener((status) {
      if(status == AnimationStatus.completed) {
          _boxController.reverse();
      } else if (status == AnimationStatus.dismissed) {
        _boxController.forward();
      }
    });
    _boxController.forward();
    /////////////////////////////////
    _catController = AnimationController(
      duration: const Duration(
        milliseconds: 200,
      ),
      vsync: this,
    );
    _catAnimation = Tween(begin: -35.0, end: -80.0,)
        .animate(
      CurvedAnimation(
        parent: _catController,
        curve: Curves.easeIn,
      ),
    );
  }

  @override
  Widget build(BuildContext context) {
    return GestureDetector(
      child: Center(
        child: Stack(
          clipBehavior: Clip.none,
          children: [
            buildCatAnimation(),
            buildBox(),
            buildLeftFlap(),
            buildRightFlap()
            // buildRightFlap()
          ],
        ),
      ),
      behavior: HitTestBehavior.translucent,
      onTap: tap,
    );
  }

  void tap() {

    if (_catController.isCompleted) {
      _catController.reverse();
      _boxController.forward();
    } else if (_catController.isDismissed) {
      _catController.forward();
      _boxController.stop();
    }
  }

  Widget buildCatAnimation() {
    return AnimatedBuilder(
      animation: _catAnimation,
      builder: (context, child) {
        return Positioned(
          child: child!,
          top: _catAnimation.value,
          right: 0.0,
          left: 0.0,
        );
      },
      child: Cat(),
    );
  }

  @override
  void dispose() {
    _catController.dispose();
    super.dispose();
  }

  Widget buildBox() {
    return Container(
      height: 200.0,
      width: 200.0,
      color: Colors.brown,
    );
  }


  Widget buildLeftFlap() {
    return Positioned(
      left: 3.0,
      child: AnimatedBuilder(
          animation: _boxAnimation,
          child: Container(
        height: 10,
        width: 125.0,
        color: Colors.brown,
      ),builder: (context, child) {
         return Transform.rotate(
           child: child,
           alignment: Alignment.topLeft,
           angle: _boxAnimation.value,);
      }),
    );
  }

  Widget buildRightFlap() {
    return Positioned(
      right: 3.0,
      child: AnimatedBuilder(
        animation: _boxAnimation,
        child: Container(
          height: 10,
          width: 125.0,
          color: Colors.brown,
        ), builder: (context, child) {
          return Transform.rotate(
              child: child,
              alignment: Alignment.topRight,
              angle: -_boxAnimation.value,);
      }),
    );
  }

  // }
}

