// Spacer has priority over mainAxisAlignment
Column spacerVsMainAlignment() {
  return Column(
    // this is ignored, better remove it.
    mainAxisAlignment: MainAxisAlignment.spaceEvenly,
    children: [
      title(),
      Container(height: 120, color: Colors.green),
      Container(height: 120, color: Colors.blue),
      Container(height: 120, color: Colors.red),
      const Spacer(),
    ],
  );
}

// The primaryColor property affects the background color for significant parts
// of the app, including toolbars, tab bars, and app bars.
//
// The accentColor property affects the foreground color for widgets, and also
// refers to the secondary color. The font family, as well as the different 
// categories for the textTheme, have all been modified, as seen in the code block above.

MaterialApp buildMaterialApp3FromSeed() {
    return MaterialApp(
       title: $TITLE$,
      themeMode: ThemeMode.light,
      theme: ThemeData(
        colorScheme: ColorScheme.fromSeed(
          // seedColor: Colors.green,
          seedColor: greenMaterialColor,
          brightness: Brightness.light,
          // primary: orangeMaterialColor,
          // secondary: greyMaterialColor,
        ),
        useMaterial3: true,
      ),
      darkTheme: ThemeData(
        colorScheme: ColorScheme.fromSeed(
          // seedColor: Colors.greenAccent,
          seedColor: orangeMaterialColor,
          brightness: Brightness.dark,
          // primary: orangeMaterialColor,
          // secondary: greyMaterialColor,
        ),
        useMaterial3: true,
      ),
      home: const $MAIN_SCREEN$,
    );
  }

ThemeData darkThemeData() {
  return ThemeData(
    colorScheme: ColorScheme.fromSwatch(
      primarySwatch: greyMaterialColor,
      brightness: Brightness.dark,
    ).copyWith(
      secondary: orangeMaterialColor,
    ),
  );
}

ThemeData lightThemeData() {
  return ThemeData(
    colorScheme: ColorScheme.fromSwatch(
      primarySwatch: greyMaterialColor,
    ).copyWith(
      secondary: orangeMaterialColor,
    ),
  );
}

MaterialApp buildMaterialAppFlexColorScheme() {
    return MaterialApp(
      title: 'Flutter Demo',
      // The Mandy red, light theme.
      theme: FlexThemeData.light(scheme: FlexScheme.sanJuanBlue),
      // The Mandy red, dark theme.
      darkTheme: FlexThemeData.dark(scheme: FlexScheme.mandyRed),
      // Use dark or light theme based on system setting.
      themeMode: ThemeMode.light,
      home: const home: $MAIN_SCREEN$,
    );
  }

MaterialApp buildMaterialApp3() {
      debugShowCheckedModeBanner: false,
    return MaterialApp(
      title: $TITLE$,
      theme: ThemeData(
        colorSchemeSeed: blueMaterialColor,
        useMaterial3: true,
      ),
      home: $MAIN_SCREEN$,
    );
  }
